class CategorySidebar extends HTMLElement{constructor(){super(),this.categories=[],this.totalPosts=0}connectedCallback(){this.render();const n=this.getAttribute("categories-data"),t=this.getAttribute("total-posts");n&&this.setCategoriesData(n),t&&this.setTotalPosts(t)}static get observedAttributes(){return["categories-data","total-posts"]}attributeChangedCallback(n,t,e){"categories-data"===n&&e&&this.setCategoriesData(e),"total-posts"===n&&e&&this.setTotalPosts(e)}setCategoriesData(n){try{this.categories=JSON.parse(n),this.render()}catch(n){this.categories=[],this.render()}}setTotalPosts(n){this.totalPosts=parseInt(n,10)||0,this.render()}formatCategoryName(n){return n.split("-").map(n=>n.charAt(0).toUpperCase()+n.slice(1)).join(" ")}createCategoryLink(n,t){return` <li class="category-item"> <a href="/category/${n}/" class="category-link"> <span class="category-name">${this.formatCategoryName(n)}</span> <span class="category-count">${t}</span> </a> </li> `}render(){const n=" <style> .category-navigation{background:#f9f9f9;border:1px solid #ddd;padding:1rem;border-radius:0.5rem;}.category-navigation h3{margin:0 0 0.8rem 0;font-size:1rem;font-weight:600;color:#1e293b;border-bottom:1px solid #ddd;padding-bottom:0.5rem;}.category-list{list-style:none;padding:0;margin:0 0 1.5rem 0;}.category-item{margin-bottom:0.5rem;}.category-link{display:flex;justify-content:space-between;align-items:center;padding:0.5rem 0;text-decoration:none;color:#555;border-bottom:1px solid #f0f0f0;transition:color 0.15s ease;}.category-link:hover{color:#333;text-decoration:underline;}.category-name{font-size:0.875rem;}.category-count{color:#6c757d;font-size:0.75rem;font-weight:400;}.category-link:hover .category-count{color:#333;}.total-posts{display:flex;justify-content:space-between;align-items:center;padding:0.5rem 0;border-top:1px solid #ddd;margin-top:0.5rem;color:#6c757d;font-size:0.875rem;}.total-label{font-weight:bold;}.total-count{color:#777;}@media (max-width:768px){.category-navigation{padding:1rem;}.category-link{padding:0.6rem;}}@media (max-width:480px){.category-navigation{margin:0 -1rem;border-radius:0;border-left:none;border-right:none;}}</style> ";if(0===this.categories.length&&0===this.totalPosts)return void(this.innerHTML=` ${n}<div class="category-navigation"> <h3>Categories</h3> <ul class="category-list"> <li class="category-item">Loading...</li> </ul> <div class="total-posts"> <span class="total-label">Total Posts</span> <span class="total-count">0</span> </div> </div> `);const t=this.categories.sort((n,t)=>n.name.localeCompare(t.name)).map(n=>this.createCategoryLink(n.name,n.count)).join("");this.innerHTML=` ${n}<div class="category-navigation"> <h3>Categories</h3> <ul class="category-list"> ${t||'<li class="category-item">No categories found</li>'}</ul> <div class="total-posts"> <span class="total-label">Total Posts</span> <span class="total-count">${this.totalPosts}</span> </div> </div> `}}customElements.define("category-sidebar",CategorySidebar);